import React, { useState, useEffect } from 'react';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  Button,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Chip,
  Box,
  Alert,
  TextField,
  Checkbox,
  FormControlLabel,
  List,
  ListItem,
  ListItemText,
  ListItemSecondaryAction,
  IconButton,
  CircularProgress
} from '@mui/material';
import {
  Add as AddIcon,
  Delete as DeleteIcon,
  PlayArrow as StartIcon,
  Stop as CompleteIcon,
  Person as PlayerIcon
} from '@mui/icons-material';
import { useApi } from '../contexts/ApiContext';
import { useAuth } from '../contexts/AuthContext';

function MatchAdmin() {
  const [matches, setMatches] = useState([]);
  const [games, setGames] = useState([]);
  const [players, setPlayers] = useState([]);
  const [users, setUsers] = useState([]);
  const [selectedMatch, setSelectedMatch] = useState(null);
  const [userParticipation, setUserParticipation] = useState(new Set()); // Track which matches user has joined
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // Dialog states
  const [createDialogOpen, setCreateDialogOpen] = useState(false);
  const [participantsDialogOpen, setParticipantsDialogOpen] = useState(false);
  const [completeDialogOpen, setCompleteDialogOpen] = useState(false);

  // Form states
  const [newMatch, setNewMatch] = useState({
    game_id: '',
    mini_admin_id: '',
    time_factor: 1.0
  });
  const [selectedPlayers, setSelectedPlayers] = useState([]);
  const [matchResults, setMatchResults] = useState({
    winners: [],
    mvp_id: '',
    jokers_played: []
  });

  const { 
    getMatches, 
    getGames, 
    getPlayers,
    getUsers,
    createMatch, 
    addParticipant, 
    removeParticipant,
    completeMatch,
    updateMatchStatus,
    deleteMatch,
    getMatch,
    joinMatch,
    leaveMatch
  } = useApi();
  const { user } = useAuth();

  useEffect(() => {
    loadData();
  }, [user]);

  const loadData = async () => {
    try {
      setLoading(true);

      // Load basic data that all users can access
      const [matchesData, gamesData, playersData] = await Promise.all([
        getMatches(),
        getGames(),
        getPlayers()
      ]);

      setMatches(matchesData);
      setGames(gamesData);
      setPlayers(playersData);

      // Check user participation in matches
      const participationSet = new Set();
      for (const match of matchesData) {
        try {
          const fullMatch = await getMatch(match.id);
          const isUserParticipating = fullMatch.participants?.some(p => p.username === user?.username);
          if (isUserParticipating) {
            participationSet.add(match.id);
          }
        } catch (err) {
          console.warn(`Could not check participation for match ${match.id}:`, err);
        }
      }
      setUserParticipation(participationSet);

      // Only load users data for admin users (needed for mini-admin dropdown)
      if (user?.role === 'admin' || user?.role === 'mini_admin') {
        try {
          const usersData = await getUsers();
          setUsers(usersData);
        } catch (userErr) {
          console.warn('Could not load users data:', userErr);
          setUsers([]);
        }
      } else {
        setUsers([]);
      }
    } catch (err) {
      console.error('Error loading data:', err);
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleCreateMatch = async () => {
    try {
      await createMatch(newMatch);
      setCreateDialogOpen(false);
      setNewMatch({ game_id: '', mini_admin_id: '', time_factor: 1.0 });
      loadData();
    } catch (err) {
      console.error('Error creating match:', err);
      setError('Failed to create match');
    }
  };

  const handleAddParticipants = async () => {
    try {
      for (const playerId of selectedPlayers) {
        await addParticipant(selectedMatch.id, playerId);
      }
      setParticipantsDialogOpen(false);
      setSelectedPlayers([]);
      loadData();
    } catch (err) {
      console.error('Error adding participants:', err);
      setError('Failed to add participants');
    }
  };

  const handleRemoveParticipant = async (matchId, playerId) => {
    try {
      await removeParticipant(matchId, playerId);
      loadData();
    } catch (err) {
      console.error('Error removing participant:', err);
      setError('Failed to remove participant');
    }
  };

  const handleCompleteMatch = async () => {
    try {
      await completeMatch(selectedMatch.id, matchResults);
      setCompleteDialogOpen(false);
      setMatchResults({ winners: [], mvp_id: '', jokers_played: [] });
      loadData();
    } catch (err) {
      console.error('Error completing match:', err);
      setError('Failed to complete match');
    }
  };

  const handleStatusChange = async (matchId, status) => {
    try {
      await updateMatchStatus(matchId, status);
      loadData();
    } catch (err) {
      console.error('Error updating status:', err);
      setError('Failed to update match status');
    }
  };

  const handleDeleteMatch = async (matchId) => {
    if (window.confirm('Are you sure you want to delete this match?')) {
      try {
        await deleteMatch(matchId);
        loadData();
      } catch (err) {
        console.error('Error deleting match:', err);
        setError('Failed to delete match');
      }
    }
  };

  const openParticipantsDialog = async (match) => {
    const fullMatch = await getMatch(match.id);
    setSelectedMatch(fullMatch);
    setParticipantsDialogOpen(true);
  };

  const openCompleteDialog = async (match) => {
    const fullMatch = await getMatch(match.id);
    setSelectedMatch(fullMatch);
    setCompleteDialogOpen(true);
  };

  const handleJoinMatch = async (matchId) => {
    try {
      await joinMatch(matchId);
      // Update participation state immediately
      setUserParticipation(prev => new Set([...prev, matchId]));
      loadData();
    } catch (err) {
      console.error('Error joining match:', err);
      if (err.response?.status === 409) {
        setError('Already joined this match');
      } else {
        setError('Failed to join match');
      }
    }
  };

  const handleLeaveMatch = async (matchId) => {
    try {
      await leaveMatch(matchId);
      // Update participation state immediately
      setUserParticipation(prev => {
        const newSet = new Set(prev);
        newSet.delete(matchId);
        return newSet;
      });
      loadData();
    } catch (err) {
      console.error('Error leaving match:', err);
      setError('Failed to leave match');
    }
  };

  // Check if current user can manage this match
  const canManageMatch = (match) => {
    return user?.role === 'admin' || match.mini_admin_id === user?.id;
  };

  // Check if current user is participating in this match
  const isParticipating = (match) => {
    return userParticipation.has(match.id);
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'warning';
      case 'in_progress': return 'info';
      case 'completed': return 'success';
      default: return 'default';
    }
  };

  if (loading) {
    return (
      <Container>
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <CircularProgress />
        </Box>
      </Container>
    );
  }

  return (
    <Container maxWidth="lg">
      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h3" component="h1">
          Match Administration
        </Typography>
        {(user?.role === 'admin' || user?.role === 'mini_admin') && (
          <Button
            variant="contained"
            startIcon={<AddIcon />}
            onClick={() => setCreateDialogOpen(true)}
          >
            Create Match
          </Button>
        )}
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3 }} onClose={() => setError('')}>
          {error}
        </Alert>
      )}

      <Grid container spacing={3}>
        {matches.map((match) => (
          <Grid item xs={12} md={6} key={match.id}>
            <Card>
              <CardContent>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', mb: 2 }}>
                  <Typography variant="h6" gutterBottom>
                    {match.game_name}
                  </Typography>
                  <Chip 
                    label={match.status} 
                    color={getStatusColor(match.status)}
                    size="small"
                  />
                </Box>

                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Players: {match.player_count} • Pot: {match.pot} tickets
                </Typography>

                <Typography variant="body2" color="text.secondary" gutterBottom>
                  Admin: {match.admin_username}
                  {match.mini_admin_username && (
                    <> • Mini-Admin: {match.mini_admin_username}</>
                  )}
                </Typography>

                <Box sx={{ display: 'flex', gap: 1, mt: 2, flexWrap: 'wrap' }}>
                  {/* Management buttons for admins and mini-admins */}
                  {canManageMatch(match) && (
                    <>
                      {match.status === 'pending' && (
                        <>
                          <Button
                            size="small"
                            startIcon={<PlayerIcon />}
                            onClick={() => openParticipantsDialog(match)}
                          >
                            Manage Players
                          </Button>
                          <Button
                            size="small"
                            startIcon={<StartIcon />}
                            onClick={() => handleStatusChange(match.id, 'in_progress')}
                          >
                            Start
                          </Button>
                        </>
                      )}

                      {match.status === 'in_progress' && (
                        <Button
                          size="small"
                          startIcon={<CompleteIcon />}
                          onClick={() => openCompleteDialog(match)}
                          color="success"
                        >
                          Complete
                        </Button>
                      )}

                      {match.status !== 'completed' && (
                        <Button
                          size="small"
                          startIcon={<DeleteIcon />}
                          onClick={() => handleDeleteMatch(match.id)}
                          color="error"
                        >
                          Delete
                        </Button>
                      )}
                    </>
                  )}

                  {/* Join/Leave buttons for all users */}
                  {match.status === 'pending' && (
                    <>
                      {!isParticipating(match) ? (
                        <Button
                          size="small"
                          variant="outlined"
                          onClick={() => handleJoinMatch(match.id)}
                          color="primary"
                        >
                          Join Match
                        </Button>
                      ) : (
                        <Button
                          size="small"
                          variant="outlined"
                          onClick={() => handleLeaveMatch(match.id)}
                          color="secondary"
                        >
                          Leave Match
                        </Button>
                      )}
                    </>
                  )}
                </Box>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Create Match Dialog */}
      <Dialog open={createDialogOpen} onClose={() => setCreateDialogOpen(false)} maxWidth="sm" fullWidth>
        <DialogTitle>Create New Match</DialogTitle>
        <DialogContent>
          <FormControl fullWidth margin="normal">
            <InputLabel>Game</InputLabel>
            <Select
              value={newMatch.game_id}
              onChange={(e) => setNewMatch({ ...newMatch, game_id: e.target.value })}
            >
              {games.map((game) => (
                <MenuItem key={game.id} value={game.id}>
                  {game.name} ({game.type})
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <FormControl fullWidth margin="normal">
            <InputLabel>Mini-Admin (Match Manager)</InputLabel>
            <Select
              value={newMatch.mini_admin_id}
              onChange={(e) => setNewMatch({ ...newMatch, mini_admin_id: e.target.value })}
            >
              <MenuItem value="">Default (michal)</MenuItem>
              {users.map((userItem) => (
                <MenuItem key={userItem.id} value={userItem.id}>
                  {userItem.username} ({userItem.role})
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <TextField
            margin="normal"
            fullWidth
            label="Time Factor"
            type="number"
            step="0.1"
            value={newMatch.time_factor}
            onChange={(e) => setNewMatch({ ...newMatch, time_factor: parseFloat(e.target.value) })}
            helperText="Multiplier for longer matches (e.g., 1.5 for CS matches)"
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCreateDialogOpen(false)}>Cancel</Button>
          <Button onClick={handleCreateMatch} variant="contained">Create</Button>
        </DialogActions>
      </Dialog>

      {/* Participants Dialog */}
      <Dialog open={participantsDialogOpen} onClose={() => setParticipantsDialogOpen(false)} maxWidth="md" fullWidth>
        <DialogTitle>Manage Participants - {selectedMatch?.game_name}</DialogTitle>
        <DialogContent>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <Typography variant="h6" gutterBottom>Current Participants</Typography>
              <List>
                {selectedMatch?.participants?.map((participant) => (
                  <ListItem key={participant.player_id}>
                    <ListItemText primary={participant.player_name} />
                    <ListItemSecondaryAction>
                      <IconButton 
                        edge="end" 
                        onClick={() => handleRemoveParticipant(selectedMatch.id, participant.player_id)}
                      >
                        <DeleteIcon />
                      </IconButton>
                    </ListItemSecondaryAction>
                  </ListItem>
                ))}
              </List>
            </Grid>

            <Grid item xs={12} md={6}>
              <Typography variant="h6" gutterBottom>Add Players</Typography>
              <FormControl fullWidth>
                <InputLabel>Select Players</InputLabel>
                <Select
                  multiple
                  value={selectedPlayers}
                  onChange={(e) => setSelectedPlayers(e.target.value)}
                  renderValue={(selected) => (
                    <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                      {selected.map((playerId) => {
                        const player = players.find(p => p.id === playerId);
                        return <Chip key={playerId} label={player?.name} size="small" />;
                      })}
                    </Box>
                  )}
                >
                  {players
                    .filter(player => !selectedMatch?.participants?.some(p => p.player_id === player.id))
                    .map((player) => (
                      <MenuItem key={player.id} value={player.id}>
                        {player.name}
                      </MenuItem>
                    ))}
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setParticipantsDialogOpen(false)}>Close</Button>
          <Button onClick={handleAddParticipants} variant="contained" disabled={selectedPlayers.length === 0}>
            Add Selected
          </Button>
        </DialogActions>
      </Dialog>

      {/* Complete Match Dialog */}
      <Dialog open={completeDialogOpen} onClose={() => setCompleteDialogOpen(false)} maxWidth="md" fullWidth>
        <DialogTitle>Complete Match - {selectedMatch?.game_name}</DialogTitle>
        <DialogContent>
          <Typography variant="h6" gutterBottom>Select Winners</Typography>
          <Box sx={{ mb: 3 }}>
            {selectedMatch?.participants?.map((participant) => (
              <FormControlLabel
                key={participant.player_id}
                control={
                  <Checkbox
                    checked={matchResults.winners.includes(participant.player_id)}
                    onChange={(e) => {
                      if (e.target.checked) {
                        setMatchResults({
                          ...matchResults,
                          winners: [...matchResults.winners, participant.player_id]
                        });
                      } else {
                        setMatchResults({
                          ...matchResults,
                          winners: matchResults.winners.filter(id => id !== participant.player_id)
                        });
                      }
                    }}
                  />
                }
                label={participant.player_name}
              />
            ))}
          </Box>

          <FormControl fullWidth margin="normal">
            <InputLabel>MVP (Optional)</InputLabel>
            <Select
              value={matchResults.mvp_id}
              onChange={(e) => setMatchResults({ ...matchResults, mvp_id: e.target.value })}
            >
              <MenuItem value="">None</MenuItem>
              {selectedMatch?.participants?.map((participant) => (
                <MenuItem key={participant.player_id} value={participant.player_id}>
                  {participant.player_name}
                </MenuItem>
              ))}
            </Select>
          </FormControl>

          <Typography variant="h6" gutterBottom sx={{ mt: 2 }}>Jokers Played</Typography>
          <Box>
            {selectedMatch?.participants?.map((participant) => {
              const player = players.find(p => p.id === participant.player_id);
              return (
                <FormControlLabel
                  key={participant.player_id}
                  control={
                    <Checkbox
                      disabled={player?.joker_used}
                      checked={matchResults.jokers_played.includes(participant.player_id)}
                      onChange={(e) => {
                        if (e.target.checked) {
                          setMatchResults({
                            ...matchResults,
                            jokers_played: [...matchResults.jokers_played, participant.player_id]
                          });
                        } else {
                          setMatchResults({
                            ...matchResults,
                            jokers_played: matchResults.jokers_played.filter(id => id !== participant.player_id)
                          });
                        }
                      }}
                    />
                  }
                  label={`${participant.player_name} ${player?.joker_used ? '(Already Used)' : ''}`}
                />
              );
            })}
          </Box>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setCompleteDialogOpen(false)}>Cancel</Button>
          <Button 
            onClick={handleCompleteMatch} 
            variant="contained" 
            disabled={matchResults.winners.length === 0}
          >
            Complete Match
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

export default MatchAdmin;
